import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const App = () => {
  const [todos, setTodos] = useState([
    { id: 1, comment: "react 모의고사" },
    { id: 2, comment: "react 모의고사" },
    { id: 3, comment: "react 모의고사" },
  ]);
  const list = {
    id: todos.length+1,
    comment: "",
  }
  const onSubmitHandler = (event) => {
    setTodos([...todos, {...todos, id: list.id}])
    setTodos(list)
  }

  return (
    <div className="App">
      <input 
        type="text" 
        name="comment" value={todos.comment} />
    </div>
  );
}

export default App;
